class Dashboard extends CI_Controller{
    function __construct()
    {
        parent::__construct();
        $this->load->model('Dashboard_model');
        $this->isUserLoggedIn = $this->session->userdata('isUserLoggedIn');
        
    }

    function index()
    {
        if($this->isUserLoggedIn){ 
            $data['total_tax_invoice'] = $this->Dashboard_model->total_tax_invoice();
            $data['total_received_amount'] = $this->Dashboard_model->total_received_amount();
            $data['today_tax_invoice'] = $this->Dashboard_model->today_tax_invoice();
            $data['today_amount'] = $this->Dashboard_model->today_amount();
            $data['total_quotation'] = $this->Dashboard_model->total_quotation();
            $data['today_quotation'] = $this->Dashboard_model->today_quotation();
            $data['tax_invoice_due'] = $this->Dashboard_model->tax_invoice_due();
            $data['disapproved_quotation'] = $this->Dashboard_model->disapproved_quotation();
            //exit;
            $data['_view'] = 'dashboard';
            $this->load->view('layouts/main',$data);
        }else{
            redirect('admin/'); 
        }
        
    }
}
