class User extends CI_Controller{
    function __construct()
    {
        parent::__construct();
        $this->load->model('User_model');
        $this->isUserLoggedIn = $this->session->userdata('isUserLoggedIn');
    } 

    /*
     * Listing of User
     */
    function index()
    {
        if($this->isUserLoggedIn){ 

        $data['user'] = $this->User_model->get_all_User();
        
        $data['_view'] = 'user/index';
        $this->load->view('layouts/main',$data);
        }else{
            redirect('login/index'); 
        }
        
    }

    /*
     * Adding a new User
     */
    function add()
    {   
        if($this->isUserLoggedIn){ 

        if(isset($_POST) && count($_POST) > 0)     
        {   
            $params = array(
				'name' => $this->input->post('name'),
				'emp_id' => $this->input->post('emp_id'),
				'username' => $this->input->post('username'),
				'password' => md5($this->input->post('password')),
				'status' => "1",
				'roles' => $this->input->post('role'),
				
            );
            
            $User_id = $this->User_model->add_User($params);
			$this->session->set_flashdata('response',"User Created Successfully");
            redirect('user/index');
        }
        else
        {            
            $data['_view'] = 'user/add';
            $this->load->view('layouts/main',$data);
        }
        }else{
            redirect('login/index'); 
        }
        
    }  

    /*
     * Editing a User
     */
    function edit($id)
    {   
        if($this->isUserLoggedIn){ 

        // check if the User exists before trying to edit it
        $data['user'] = $this->User_model->get_User($id);
        
        if(isset($data['user']['id']))
        {
            if(isset($_POST) && count($_POST) > 0)     
            {   
                $params = array(
					'name' => $this->input->post('name'),
					'username' => $this->input->post('username'),
					'password' => md5($this->input->post('password')),
					'emp_id' => $this->input->post('emp_id'),
					'roles' => $this->input->post('role'),
					
                );

                $this->User_model->update_User($id,$params);  
				$this->session->set_flashdata('response',"User Modified Successfully");
                redirect('user/index');
            }
            else
            {
                $data['_view'] = 'user/edit';
                $this->load->view('layouts/main',$data);
            }
        }
        else
            show_error('The User you are trying to edit does not exist.');
        }else{
            redirect('login/index'); 
        }
        
    } 

    /*
     * Deleting User
     */
    function remove($id)
    {
        if($this->isUserLoggedIn){ 

        $User = $this->User_model->get_User($id);

        // check if the User exists before trying to delete it
        if(isset($User['id']))
        {
            $this->User_model->delete_User($id);
			$this->session->set_flashdata('response',"User Deleted Successfully");
            redirect('User/index');
        }
        else
            show_error('The User you are trying to delete does not exist.');
        }else{
            redirect('login/index'); 
        }
        
    }
    
}
