    }
        
    /*
     * Get all invoice
     */
    function get_all_invoice()
    {
        //$this->db->order_by('id', 'desc');
         $this->db->select('invoice.id, invoice.quotation_no, invoice.invoice_no,invoice.invoice_date,quotation.customer_name,(select sum(total) from item_service_list where item_service_list.quotation_no=invoice.quotation_no) as total')
         ->from('invoice')
         ->join('quotation', 'invoice.quotation_no = quotation.quotation_no','left')
          ->join('item_service_list', 'item_service_list.quotation_no=quotation.quotation_no','left');
           $this->db->distinct();
        return $this->db->get()->result_array();
    }

