    /*
     * Get all quotation invoice
     */
    function get_quotation_no()
    {
        
         $this->db->select('quotation_no')
         ->from('quotation')
         ->where('approval_status IN ("Approved") AND invoice_generated=""')
         ->order_by('id', 'desc');
        return $this->db->get()->result_array();
    }
        
